import styled from '@emotion/styled';
import './App.css';
import Banner from './assets/imagen-criptos.png'

function App() {

const Contenedor = styled.div`
  max-width: 900px;
  margin: 0 auto;
`
const Heading = styled.h1`
  font-size: 24px;
  color: #fff;
  text-align: center;
  margin-top: 25px;
`
const Imagen = styled.img`
  width: 40%;
  padding: 15px 90px;
`


  return (
       <>


       <Contenedor> 

           <Heading> Cotizador de criptomonedas</Heading>
           <Imagen src={Banner} alt="imagen-cripto" > </Imagen>  

       </Contenedor>

       </>
  );
}

export default App;
