import React from 'react'
import styled from '@emotion/styled';

const Label = styled.label`
    color: #fff;
    display: block;
    font-size: 24px;
`

const Select = styled.select`
    width: 300px;
    font-size: 18px;
    padding: 5px;
    margin-top: 10px;
    margin-bottom: 20px;
` 

const useSelectMonedas = (label, opciones) => {

    const SelectMonedas = () => ( 
       <>
         <Label> {label} </Label>

         <Select>
              <option value="">Seleccione</option>
              { opciones.map( opcion => (
                <option
                 key={opcion.id}
                 value={opcion.id}
                > 
                 {opcion.nombre}
                </option>
              )) }
         </Select>
      
       </>
    )

     return [ SelectMonedas ]

}

export default useSelectMonedas
